mapper.update:
update
VERSION = #{version}+1 , USER_NO = #{userNo}, USER_NAME = #{userName}, USER_TYPE = #{userType}, CAL_FEE_ITEM = #{calFeeItem}, PAY_PRODUCT = #{payProduct}, FRP_CODE = #{frpCode}, EFFECTIVE_START = #{effectiveStart}, EFFECTIVE_END = #{effectiveEnd}, CREATE_RULE = #{createRule}, FEE_RULE = #{feeRule}
ID = #{id} and VERSION = #{version}

mapper.select:
select * from


	USER_NO = #{userNo}
	AND
	USER_TYPE = #{userType}
	AND
	CAL_FEE_ITEM = #{calculateFeeItem}
	and PAY_PRODUCT is null
	and PAY_PRODUCT = #{payProduct}
	and FRP_CODE is null
	and FRP_CODE = #{frpCode}
	and EFFECTIVE_START = #{start} and EFFECTIVE_END = #{end}
	and EFFECTIVE_START <= #{start, jdbcType=TIMESTAMP} and EFFECTIVE_END >= #{start, jdbcType=TIMESTAMP}
