mapper.select:
select VAR_1 as "merchantNo", VAR_2 AS "merchantName", sum(INT_1) as "debitCount",sum(DEC_1) as "debitAmount",sum(INT_2) as "creditCount", sum(DEC_2) as "creditAmount", sum(INT_3) as "totalCount",sum(DEC_3) as "totalAmount", sum(DEC_20) as "t0Fee", INT_4 as "settType" from
group by VAR_1 , INT_4 order by totalAmount desc
HAVING totalAmount >=#{beginAmount,jdbcType=NUMERIC} and totalAmount <=#{endAmount,jdbcType=NUMERIC}
HAVING totalAmount >=#{beginAmount,jdbcType=NUMERIC} HAVING totalAmount <=#{endAmount,jdbcType=NUMERIC}

select VAR_1 as "merchantNo", VAR_2 AS "merchantName", INT_4 as "settType", sum(DEC_20) as "t0Fee", sum(INT_3) as "totalCount", sum(DEC_3) as "totalAmount" from
group by INT_4
HAVING totalAmount >=#{beginAmount,jdbcType=NUMERIC} and totalAmount <=#{endAmount,jdbcType=NUMERIC}
HAVING totalAmount >=#{beginAmount,jdbcType=NUMERIC} HAVING totalAmount <=#{endAmount,jdbcType=NUMERIC}

select T.ID as "id", T.TRX_DATE as "trxDate", T.VAR_1 as "merchantNo", T.VAR_2 as "merchantName", T.INT_3 as "totalCount", T.DEC_3 as "totalAmount", T.DEC_11 as "totalFee", T.DEC_12 as "settAmount", T.VAR_9 as "bankAccountNo", T.VAR_10 as "bankAccountName", T.VAR_11 as "bankChannelNo", T.VAR_12 as "bankName", U.BANKACCOUNTTYPE as "bankAccountType", T.INT_4 AS "settType", T.DEC_20 AS "t0Fee", T.VAR_13 AS "channelNo", T.VAR_14 AS "channelName" from T left join TBL_USER_BANK_ACCOUNT U ON T.VAR_1=U.USERNO
and T.VAR_2 like '%${merchantName}%'
and T.VAR_1 = #{merchantNo}
and T.VAR_13 = #{channelNo}
order by TRX_DATE desc, DEC_3 desc

select sum(T.INT_3) AS "totalCount", sum(T.DEC_3) AS "totalAmount", sum(T.DEC_11) AS "totalFee", sum(T.DEC_12) AS "settAmount", sum(case when T.INT_4 = 1 then T.DEC_20 when T.INT_4 != 1 then 0 else T.INT_4 end) as "t0Fee"
from T left join TBL_USER_BANK_ACCOUNT U ON T.VAR_1=U.USERNO
and T.VAR_2 like '%${merchantName}%'
and T.VAR_1 = #{merchantNo}
and T.VAR_13 = #{channelNo}

select sum(temp.totalCount) as "totalCount", SUM(temp.totalAmount) as "totalAmount" from ( select TRX_DATE as "trxDate", VAR_1 as "merchantNo", VAR_2 as "merchantName", VAR_3 as "firstAgentNo", VAR_5 as "secondAgentNo", VAR_7 as "thirdAgentNo", VAR_4 as "firstAgentName", VAR_6 as "secondAgentName", VAR_8 as "thirdAgentName", sum(sum(INT_3) as "totalCount", sum(sum(DEC_3) as "totalAmount" from
GROUP BY TRX_DATE , VAR_1 ) temp
temp.merchantNo = #{merchantNo}

select t1.*,t2.totalCount,t2.totalAmount,t2.totalFee,t2.platProfit from (select SALES_NO as "salesNo" ,SALES_NAME as "salesName" ,count(1) as "agentNum" from
and SALES_NO is not null and SALES_NO!='' and `LEVEL`=1 and SALES_NO = #{salesNo}
GROUP BY SALES_NO)t1 left join (select SALES_NO , SALES_NAME , sum(INT_3) as "totalCount", sum(DEC_3) as "totalAmount", sum(DEC_11) as "totalFee" , sum(DEC_14) as "platProfit" from a left join b on a.AGENT_NO = b.VAR_3
and REPORT_TYPE = #{reportType}
GROUP BY a.SALES_NO )t2 on t1.salesNo=t2.SALES_NO
