mapper.update:
update
VERSION = #{version}+1, NAME = #{name,jdbcType=VARCHAR}, URL = #{url,jdbcType=VARCHAR}, PARENTID = #{parent.id,jdbcType=VARCHAR}, NUMBER = #{number,jdbcType=VARCHAR}, ISLEAF = #{isLeaf,jdbcType=BOOLEAN}, LEVEL =#{level,jdbcType=INTEGER}, TARGETNAME = #{targetName,jdbcType=VARCHAR}
where ID = #{id} and VERSION = #{version}

mapper.select:
select M.ID AS "id", M.VERSION AS "version", M.PARENTID AS "pId", M.NAME as "name", M.ISLEAF AS "isLeaf", M.LEVEL AS "level", M.TARGETNAME AS "targetName" from M
And M.PARENTID = #{parentId}
order by M.NUMBER asc
